t.script:
          document.addEventListener('DOMContentLoaded', function () {
            if (window.AOS) {
              AOS.init({ duration: 800, easing: 'ease-in-out', once: true, offset: 100 });
            }

            const siteHeader = document.querySelector('#wrapwrap header');
            const scrollTopBtn = document.getElementById('rmcScrollTop');

            document.querySelectorAll('a[href^="#"]').forEach(anchor => {
              anchor.addEventListener('click', function (e) {
                const targetSelector = this.getAttribute('href');
                if (!targetSelector || targetSelector === '#') {
                  return;
                }
                const target = document.querySelector(targetSelector);
                if (target) {
                  e.preventDefault();
                  const headerHeight = siteHeader ? siteHeader.offsetHeight : 0;
                  const top = target.getBoundingClientRect().top + window.pageYOffset - headerHeight - 10;
                  window.scrollTo({ top, behavior: 'smooth' });
                }
              });
            });

            const handleScroll = () => {
              if (scrollTopBtn) {
                if (window.scrollY > 300) {
                  scrollTopBtn.classList.add('visible');
                } else {
                  scrollTopBtn.classList.remove('visible');
                }
              }
            };

            window.addEventListener('scroll', handleScroll);
            handleScroll();

            if (scrollTopBtn) {
              scrollTopBtn.addEventListener('click', () => {
                window.scrollTo({ top: 0, behavior: 'smooth' });
              });
            }

            const faqItems = document.querySelectorAll('.rmc-faq-item');
            faqItems.forEach(item => {
              const question = item.querySelector('.rmc-faq-question');
              question.setAttribute('tabindex', '0');
              question.setAttribute('role', 'button');
              question.setAttribute('aria-expanded', 'false');

              question.addEventListener('click', () => {
                faqItems.forEach(other => {
                  if (other !== item) {
                    other.classList.remove('active');
                    const q = other.querySelector('.rmc-faq-question');
                    if (q) {
                      q.setAttribute('aria-expanded', 'false');
                    }
                  }
                });
                item.classList.toggle('active');
                question.setAttribute('aria-expanded', item.classList.contains('active') ? 'true' : 'false');
              });

              question.addEventListener('keydown', (e) => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  question.click();
                }
              });
            });

            const rmcSalesInput = document.getElementById('rmcSales');
            const aacOrdersInput = document.getElementById('aacOrders');
            const equipmentDealsInput = document.getElementById('equipmentDeals');
            const materialsSalesInput = document.getElementById('materialsSales');
            const resultAmount = document.getElementById('rmcResultAmount');
            const resultBreakdown = document.getElementById('rmcResultBreakdown');
            const resultSection = document.getElementById('rmcEarningsResult');
            const calculateBtn = document.getElementById('rmcCalculateBtn');

            const formatCurrency = (amount) => {
              return new Intl.NumberFormat('en-IN', {
                style: 'currency',
                currency: 'INR',
                maximumFractionDigits: 0
              }).format(amount);
            };

            const calculateEarnings = () => {
              const rmcSales = parseFloat(rmcSalesInput.value) || 0;
              const aacOrders = parseFloat(aacOrdersInput.value) || 0;
              const equipmentDeals = parseFloat(equipmentDealsInput.value) || 0;
              const materialsSales = parseFloat(materialsSalesInput.value) || 0;

              const rmcCommission = rmcSales * 100;
              const aacCommission = aacOrders * 0.04;
              const equipmentCommission = equipmentDeals * 0.065;
              const materialsCommission = materialsSales * 0.03;
              const totalEarnings = rmcCommission + aacCommission + equipmentCommission + materialsCommission;

              if (resultAmount) {
                resultAmount.textContent = formatCurrency(totalEarnings);
              }

              if (resultBreakdown) {
                resultBreakdown.innerHTML = `



RMC Commission:${formatCurrency(rmcCommission)}

AAC Blocks Commission:${formatCurrency(aacCommission)}

Equipment Commission:${formatCurrency(equipmentCommission)}

Materials Commission:${formatCurrency(materialsCommission)}

                `;
              }

              if (resultSection) {
                resultSection.style.display = 'block';
              }
            };

            if (calculateBtn) {
              calculateBtn.addEventListener('click', calculateEarnings);
            }
            calculateEarnings();

            const form = document.getElementById('rmcApplicationForm');
            const successMessage = document.getElementById('rmcSuccessMessage');
            const fresherFields = document.querySelector('.rmc-fresher-fields');
            const experiencedFields = document.querySelector('.rmc-experienced-fields');

            const toggleApplicantFields = (type) => {
              if (type === 'fresher') {
                fresherFields.style.display = 'block';
                experiencedFields.style.display = 'none';
                fresherFields.querySelectorAll('select, input').forEach(el => el.setAttribute('required', 'required'));
                experiencedFields.querySelectorAll('select, input').forEach(el => el.removeAttribute('required'));
              } else if (type === 'experienced') {
                fresherFields.style.display = 'none';
                experiencedFields.style.display = 'block';
                experiencedFields.querySelectorAll('select, input').forEach(el => el.setAttribute('required', 'required'));
                fresherFields.querySelectorAll('select, input').forEach(el => el.removeAttribute('required'));
              } else {
                fresherFields.style.display = 'none';
                experiencedFields.style.display = 'none';
              }
            };

            document.querySelectorAll('input[name="applicantType"]').forEach(input => {
              input.addEventListener('change', function () {
                toggleApplicantFields(this.value);
              });
            });

            const phoneInput = form ? form.querySelector('input[name="phone"]') : null;
            if (phoneInput) {
              phoneInput.addEventListener('blur', function () {
                const phoneRegex = /^[6-9]\d{9}$/;
                if (this.value && !phoneRegex.test(this.value)) {
                  this.setCustomValidity('Please enter a valid 10-digit mobile number starting with 6-9');
                } else {
                  this.setCustomValidity('');
                }
              });
              phoneInput.addEventListener('input', function () {
                this.setCustomValidity('');
              });
            }

            if (form) {
              form.addEventListener('submit', function (e) {
                e.preventDefault();
                if (!form.checkValidity()) {
                  form.reportValidity();
                  return;
                }
                const formData = new FormData(form);
                console.log('Commission agent application submitted:', Object.fromEntries(formData.entries()));
                form.style.display = 'none';
                if (successMessage) {
                  successMessage.classList.add('active');
                  successMessage.scrollIntoView({ behavior: 'smooth', block: 'center' });
                }
              });
            }

            const observer = new IntersectionObserver((entries) => {
              entries.forEach(entry => {
                if (entry.isIntersecting) {
                  entry.target.classList.add('visible');
                }
              });
            }, { threshold: 0.2 });

            document.querySelectorAll('.rmc-benefit-card, .rmc-point-card, .rmc-testimonial-card').forEach(el => {
              observer.observe(el);
            });
          });
